mapper.select:
SELECT d.id id, d.parent_id parentId, d.name name, d.header header, d.reduce_header reduceHeader, d.is_del isDel, d.status status, d.create_time createTime, d.update_time updateTime, d.sort sort, d1.name parentName, au.user_name headerName, au1.user_name reduceHeaderName FROM department d LEFT JOIN department d1 ON(d1.id = d.parent_id) INNER JOIN tp_main_admin_user au ON(au.id = d.header) INNER JOIN tp_main_admin_user au1 ON(au1.id = d.reduce_header) WHERE d.is_del = 0 AND d.status = 0
AND d.name LIKE CONCAT('%',#{departmentName},'%')
AND d.parent_id = #{departmentId}

select

from department d
where id = #{d.id,jdbcType=INTEGER}

mapper.insert:
insert into department (parent_id, name,
  header, reduce_header, is_del,
  status, create_time,
  create_user, sort
  )
values (#{parentId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR},
  #{header,jdbcType=VARCHAR}, #{reduceHeader,jdbcType=VARCHAR}, #{isDel,jdbcType=INTEGER},
  #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP},
  #{createUser,jdbcType=VARCHAR}, #{sort,jdbcType=VARCHAR}
  )
