mapper.select:
SELECT u.id AS id, u.user_name AS userName, u.login_name AS loginName, u.user_contact AS userContact, u.user_address AS userAddress, u.mail AS mail, u.create_time AS createTime, u.update_time AS updateTime, u.status AS status, u.role_id AS roleId, u.remark AS remark, u.is_admin AS isAdmin, u.is_leave AS isLeave, u.nature AS nature, u.post AS post, u.sort AS sort, p.post_name postName, u.header_url AS headerUrl, GROUP_CONCAT(DISTINCT d.id ) AS departmentId, GROUP_CONCAT(DISTINCT d.NAME ) AS departmentName, GROUP_CONCAT(DISTINCT up.post_id) AS deputyPostId, GROUP_CONCAT(DISTINCT p.post_name ORDER BY p.id) AS deputyPost, GROUP_CONCAT(DISTINCT ug.group_id) AS groupId, GROUP_CONCAT(DISTINCT g.group_name) AS groupName FROM tp_main_admin_user u LEFT JOIN user_post up ON(up.user_id = u.id) LEFT JOIN post p ON(p.id = u.post) LEFT JOIN user_department ud ON ( ud.user_id = u.id ) LEFT JOIN department d ON ( d.id = ud.department_id ) LEFT JOIN user_group ug ON (ug.user_id = u.id) LEFT JOIN tp_group g ON (g.id = ug.group_id) WHERE u.is_delete = 0 AND u.user_type = #{userType} AND u.is_leave = #{isLeave}
AND u.id = #{id}
AND d.id = #{departmentId}
AND u.user_name LIKE CONCAT('%',#{userName},'%')
AND u.nature = #{nature}
AND u.post = #{post}
GROUP BY u.id ORDER BY u.sort DESC, u.create_time DESC

mapper.update:
update
tp_main_admin_user
set
header_url = #{headerUrl}
where id = #{userId}

update
tp_main_admin_user
set login_password = #{newPassword}
where id = #{id}
